function scrollToEl(element){theElement=document.getElementById(element);theElement.scrollIntoView({behavior:"smooth"})}function updateCopyright(){const d=(new Date).getFullYear();document.getElementById("copyright").innerHTML="&copy"+d}function fadeIn(){let els=document.getElementsByClassName("appear");for(let i=0;i<els.length;++i){const delay=i*.2+"s";let anim="right";if(i%2==0){anim="left"}els[i].style.animation="appear-"+anim+" 0.8s "+delay+" ease-out forwards"}}function insertAngleSVGs(){const sections=document.getElementsByClassName("section");const svgOpen='<svg viewBox="0 0 100 10"><polygon points="';const svgClose='"></svg>';const svg2a=svgOpen+"0,0 100,0 100,10"+svgClose;const svg2b=svgOpen+"0,0 100,0 0,10"+svgClose;const svg1a=svgOpen+"0,10 100,0 100,10"+svgClose;const svg1b=svgOpen+"0,10 0,0 100,10"+svgClose;for(let i=0;i<sections.length;++i){const useB=Math.random()>=.5;let theSVG=svg1a;if(i%2!=0){if(useB){theSVG=svg1b}}else{theSVG=svg2a;if(useB){theSVG=svg2b}}sections[i].insertAdjacentHTML("afterEnd",theSVG)}sections[sections.length]}function loaded(){updateCopyright();insertAngleSVGs();fadeIn()}